import{_ as s,o as a,c as l,Q as n}from"./chunks/framework.4719a631.js";const u=JSON.parse('{"title":"指令","description":"","frontmatter":{},"headers":[],"relativePath":"guide/useful-commands.md","filePath":"guide/useful-commands.md","lastUpdated":1700569489000}'),o={name:"guide/useful-commands.md"},p=n(`<h1 id="指令" tabindex="-1">指令 <a class="header-anchor" href="#指令" aria-label="Permalink to &quot;指令&quot;">​</a></h1><p>本节介绍了主插件(<code>chatluna</code>)的用法（及其指令）。对于其他 chatluna 生态插件的指令，请参考对应的文档（？）。</p><h2 id="对话" tabindex="-1">对话 <a class="header-anchor" href="#对话" aria-label="Permalink to &quot;对话&quot;">​</a></h2><p>对话是用户和 ChatLuna 交互的主要方式，用户通过输入文本来和 ChatLuna 进行对话，ChatLuna 会根据用户的输入和当前用户所在的返回键的信息，选择好相应的模型，生成回复文本，并发送给用户。</p><h3 id="直接对话" tabindex="-1">直接对话 <a class="header-anchor" href="#直接对话" aria-label="Permalink to &quot;直接对话&quot;">​</a></h3><p>基于用户当前使用的房间，直接对话。</p><p>以下为命令格式：</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">chatluna.chat.text</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-r</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">roo</span><span style="color:#E1E4E8;">m</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">messag</span><span style="color:#E1E4E8;">e</span><span style="color:#F97583;">&gt;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">chatluna.chat.text</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-r</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">roo</span><span style="color:#24292E;">m</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">messag</span><span style="color:#24292E;">e</span><span style="color:#D73A49;">&gt;</span></span></code></pre></div><p>以下为命令的可选参数：</p><ul><li><code>-r,room</code>: 指定对话的房间，默认为用户在当前环境里使用的房间，可为房间名或房间ID。</li></ul><p>以下为例子：</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">chatluna.chat.text</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">你好，GPT！</span></span>
<span class="line"></span>
<span class="line"><span style="color:#B392F0;">chatluna.chat.text</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-r</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">猫娘</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">你是猫娘吗？</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">chatluna.chat.text</span><span style="color:#24292E;"> </span><span style="color:#032F62;">你好，GPT！</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6F42C1;">chatluna.chat.text</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-r</span><span style="color:#24292E;"> </span><span style="color:#032F62;">猫娘</span><span style="color:#24292E;"> </span><span style="color:#032F62;">你是猫娘吗？</span></span></code></pre></div><h3 id="语音回复对话" tabindex="-1">语音回复对话 <a class="header-anchor" href="#语音回复对话" aria-label="Permalink to &quot;语音回复对话&quot;">​</a></h3><p>与上面的类似，但是不同的是 ChatLuna 会尝试调用 <a href="https://github.com/initialencounter/mykoishi/blob/master/Plugins/Tool/vits/readme.md" target="_blank" rel="noreferrer">vits</a> 服务，将模型生成的内容转换成语音发送。</p><p>以下为命令格式：</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">chatluna.chat.voice</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-r</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">roo</span><span style="color:#E1E4E8;">m</span><span style="color:#F97583;">&gt;</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">messag</span><span style="color:#E1E4E8;">e</span><span style="color:#F97583;">&gt;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">chatluna.chat.voice</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-r</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">roo</span><span style="color:#24292E;">m</span><span style="color:#D73A49;">&gt;</span><span style="color:#24292E;"> </span><span style="color:#D73A49;">&lt;</span><span style="color:#032F62;">messag</span><span style="color:#24292E;">e</span><span style="color:#D73A49;">&gt;</span></span></code></pre></div><p>以下为可选参数：</p><ul><li><code>-r,--room</code>: 指定对话的房间，默认为用户在当前环境里使用的房间，可为房间名或房间ID。</li><li><code>-s,--speaker</code>: 指定使用的 vits 服务的目标音色的 ID。</li></ul><p>以下为例子：</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#B392F0;">chatluna.chat.voice</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">你好，GPT！</span></span>
<span class="line"></span>
<span class="line"><span style="color:#B392F0;">chatluna.chat.voice</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-r</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">猫娘</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">你是猫娘吗？</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#6F42C1;">chatluna.chat.voice</span><span style="color:#24292E;"> </span><span style="color:#032F62;">你好，GPT！</span></span>
<span class="line"></span>
<span class="line"><span style="color:#6F42C1;">chatluna.chat.voice</span><span style="color:#24292E;"> </span><span style="color:#005CC5;">-r</span><span style="color:#24292E;"> </span><span style="color:#032F62;">猫娘</span><span style="color:#24292E;"> </span><span style="color:#032F62;">你是猫娘吗？</span></span></code></pre></div>`,20),e=[p];function t(c,r,i,y,h,d){return a(),l("div",null,e)}const F=s(o,[["render",t]]);export{u as __pageData,F as default};
